import java.util.ArrayList;
import java.util.List;

public class Library {
    public static void addBook(String title, String author, String genre) {
        books.add(new Book(title, author, genre));
    }

    public static List<Book> searchBook(String query) {

        System.out.println(
                "==== 도서 검색 ====\n" +
                "검색 기준을 선택하세요:\n" +
                "1. 제목\n" +
                "2. 저자\n" +
                "3. 분류(KDC)\n" +
                "(현재 구현은 '제목' 기준으로만 검색합니다)\n"
        );

        List<Book> searchResults = new ArrayList<>();
        for (Book book : books) {
            if (book.title.contains(query)) {
                searchResults.add(book);
            }
        }
        return searchResults;
    }


    public record Book(String title, String author, String KDC) {}
    private static final List<Book> books = new ArrayList<>();
}

import java.util.ArrayList;
import java.util.List;

public class Library {
    public static void addBook(String title, String author, String genre) {
        books.add(new Book(title, author, genre));
    }

    public static List<Book> searchBook(String query) {

        System.out.println(
                "==== 도서 검색 ====\n" +
                "검색 기준을 선택하세요:\n" +
                "1. 제목\n" +
                "2. 저자\n" +
                "3. 분류(KDC)\n" +
                "(현재 구현은 '제목' 기준으로만 검색합니다)\n"
        );

        List<Book> searchResults = new ArrayList<>();
        for (Book book : books) {
            if (book.title.contains(query)) {
                searchResults.add(book);
            }
        }
        return searchResults;
    }


    public record Book(String title, String author, String KDC) {}
    private static final List<Book> books = new ArrayList<>();
}
